mapper.select:
SELECT f.flow_id AS flowId, f.flow_type as flowType, f.flow_name AS flowName, f.flow_status AS flowStatus, f.create_time AS createTime, u.user_name AS userName FROM tb_flow f LEFT JOIN tb_user u ON f.user_id = u.user_id WHERE 1=1
and f.flow_type=#{flowType,jdbcType=VARCHAR}
and f.flow_status=#{flowStatus,jdbcType=VARCHAR}
and u.user_name like CONCAT(CONCAT('%', #{userName,jdbcType=VARCHAR} ), '%')
and f.user_id=#{userId,jdbcType=VARCHAR}
and f.create_time >= #{beforeDate,jdbcType=VARCHAR}
order by f.create_time desc

SELECT f.flow_id AS flowId, f.flow_type as flowType, f.flow_name AS flowName, f.flow_status AS flowStatus, f.create_time AS createTime, u.user_name AS userName from tb_flow_check c LEFT JOIN tb_flow f on c.flow_id=f.flow_id LEFT JOIN tb_user u ON f.user_id = u.user_id WHERE 1=1
and f.flow_type=#{flowType,jdbcType=VARCHAR}
and f.flow_status=#{flowStatus,jdbcType=VARCHAR}
and u.user_name like CONCAT(CONCAT('%', #{userName,jdbcType=VARCHAR} ), '%')
and c.user_id=#{userId,jdbcType=VARCHAR}
and f.create_time >= #{beforeDate,jdbcType=VARCHAR}
order by f.create_time desc
